mapper.update:
update ssmbuild.bookborrow
set sta=#{sta}
where returnDate < #{now}

update ssmbuild.bookborrow
set returnDate = #{newReturnDate}
where bookBorrowId = #{bookBorrowId}

mapper.select:
SELECT * FROM ssmbuild.bookborrow


        bookId like '%${bookId}%'


        readerId like '%${readerId}%'


        sta = #{sta}


order by sta desc;
